<?php
    $config = require('./config.php');
    $attendee = new stdClass;
    if ($config['host'] && !empty($_GET) && isset($_GET['id'])) {
        $id = $_GET['id'];
        $host = $config['host'];
        $attendeeJson = file_get_contents("$host/collections/api/attendee/$id");
        $attendee = json_decode($attendeeJson);
    }
    function _getType($type) {
        switch ($type) {
            case 'allinclusive':
                $attendeeType = "All Inclusive";
                break;
            case 'guestmeal':
            case 'guest':
                $attendeeType = "Guest";
                break;
            case 'kids':
                $attendeeType = "Kids";
                break;
            case 'staff':
                $attendeeType = "Staff";
                break;
            case 'vip':
                $attendeeType = "VIP";
                break;
            default:
                $attendeeType = "none";
                break;
        }
        return $attendeeType;
    }
?>
